<template>    <transition name="ci-ajax-progress--fade">        <div class="ci-ajax-progress" v-show="progress && progress < 1">            <div class="ci-ajax-progress--bar" :style="{width: progress * 100 + '%'}"></div>        </div>    </transition></template><script>export default {    name: 'ci-ajax-progress',    props: {        progress: {            type: Number,            default: 0,        },    },};</script>