<template>    <div class="ci-card">        <div class="ci-card--header-panel" :style="styles.header">            <slot name="header"></slot>        </div>        <div class="ci-card--info-panel">            <slot></slot>        </div>        <div class="ci-card--action-panel" v-if="hasActions">            <slot name="footer"></slot>        </div>    </div></template><script>export default {    name: 'ci-card',    props: {        'styles': {            type: Object,            default: () => ({})        },    },    data() {        return {            hasActions: false,        };    },    mounted() {        this.hasActions = !!this.$slots.footer;    },};</script>