<template>    <div class="ci-offcanvas" @click="hide">        <transition name="ci-offcanvas--fade">            <div class="ci-offcanvas--bg" v-show="panelSwitcher"></div>        </transition>        <transition name="ci-offcanvas--slide-right">            <div class="ci-offcanvas--main" v-show="panelSwitcher" @click.stop>                <slot></slot>            </div>        </transition>    </div></template><script>export default {    name: 'ci-off-canvas',    data() {        return {            bodyOverflow: '',            panelSwitcher: false,        };    },    methods: {        hide() {            this.panelSwitcher = false;            window.document.body.style.overflow = this.bodyOverflow;        },        open() {            this.panelSwitcher = true;            this.bodyOverflow = window.document.body.style.overflow;            window.document.body.style.overflow = 'hidden';        },    },};</script>